stage.initialvalue:
function bp_action() {
	bp_prepare_retval();
	var grid = bp_get_grid();
	bp_set_retval(grid.GetRowCount());
}

function bp_get_grid() {
	var pegwin = document.getElementById('iapptab_Frame_0_C19').contentWindow;
	if(pegwin == null)
		return null;
	if(pegwin.fauxTab == undefined)
		return null
	var grid = pegwin.fauxTab.GetFrame('TB_PEGOVERVIEW').frames[0].igdPegBoard;
	return grid;
}

function bp_prepare_retval() {
	var input = document.getElementById('bp_retval');
	if(input != null)
		return;
	input = document.createElement('input');
	input.setAttribute('type', 'hidden');
	input.setAttribute('id', 'bp_retval');
	input.setAttribute('value', '');
	document.getElementsByTagName('body')[0].appendChild(input);
}

function bp_set_retval(value) {
	var input = document.getElementById('bp_retval');
	if(input == null)
		return;
	input.setAttribute('value', value);
}

function bp_action(rowindex, columnname) {
	bp_prepare_retval();
	var grid = bp_get_grid();
	bp_set_retval(grid.GetValueByColumnName(rowindex, columnname));
}

function bp_action(columnname, value, exact) {
	bp_prepare_retval();
	if(!exact)
		value = value.toLowerCase();
	var grid = bp_get_grid();
	var foundrow = -1;
	for(var i=0;i<grid.GetRowCount();i++) {
		var fv = grid.GetValueByColumnName(i, columnname);
		if(!exact) {
			fv = fv.toLowerCase();
			fv = fv.replace(/^\s+|\s+$/g, '');
		}
		if(fv == value) {
			foundrow = i;
			break;
		}
	}
	bp_set_retval(foundrow);
}

function bp_action(rowindex) {
	bp_prepare_retval();
	var grid = bp_get_grid();
	bp_set_retval(grid.SelectRow(rowindex));
}

function bp_action() {
	var grid = bp_get_grid();
	grid.RowUnSelectAll();
}

function bp_action() {
	bp_prepare_retval();
	var grid = bp_get_grid();
	var columns = "";
	for(i=0;i<grid.GetColumnCount(0);i++) {
		if(columns != "")
			columns += "|";
		columns += grid.GetColumnNameByIndex(i);
	}
	bp_set_retval(columns);
}
